import React, { Component } from 'react';
import {Routes,Route}  from "react-router-dom";
import cookie from 'react-cookies';
import axios from 'axios';

// css
import '../css/new.css';
// header
import HeaderAdmin from './Header/Header admin';
// footer
import Footer from './Footer/Footer';
// route
import LoginForm from './LoginForm';
import ReactProxy from './ReactProxy';
import APIGetJson from './APIGetJson';
import APIPostJson from './APIPostJson';
import SwList from './SwToolsManage/SwList';
import SwView from './SwToolsManage/SwView';
import SwDetailView from './SwToolsManage/SwDetailView';
import Register from './User/Register';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }

  componentDidMount() {
    axios.post('/api/LoginForm?type=SessionConfirm', {
      token1 : cookie.load('userid') 
      , token2 : cookie.load('username') 
    })
    .then( response => {
        this.state.userid = response.data.token1
        let password = cookie.load('userpassword')
        if(password !== undefined){
          axios.post('/api/LoginForm?type=SessionSignin', {
            is_Email: this.state.userid,
            is_Token : password
          })
          .then( response => {
            if(response.data.json[0].useremail === undefined){
              this.noPermission()
            }
          })
          .catch( error => {
            this.noPermission()
          });
        }else{
          this.noPermission()
        }
    })
    .catch( response => this.noPermission());
  }

  noPermission = (e) => {
    if(window.location.hash != 'nocookie'){
      this.remove_cookie();
      window.location.href = '/login/#nocookie';
    }
  };

  remove_cookie = (e) => {
    cookie.remove('userid', { path: '/'});
    cookie.remove('username', { path: '/'});
    cookie.remove('userpassword', { path: '/'});
  }

  render () {
    return (
      <div className="App">
        <HeaderAdmin/> 
        <Routes>
          <Route exact path='/'element = {<LoginForm/>} />
          <Route exact path='/ReactProxy' element = {<ReactProxy/>} />
          <Route exact path='/APIGetJson' element = {<APIGetJson/>} />
          <Route exact path='/APIPostJson' element = {<APIPostJson/>} />
          <Route exact path='/SwList' element = {<SwList/>} />
          <Route exact path='/SwView' element = {<SwView/>} />
          <Route path='/SwDetailView/:swtcode' element = {<SwDetailView/>} />
          <Route exact path='/Register' element = {<Register/>} />
        </Routes>
        <Footer/>
      </div>
    );
  }
}

export default App;
